import React, { useState } from 'react';

const Card = ({ initialText }) => {
  const [text, setText] = useState(initialText);

  const handleClick = () => {
    // Update the text when the card is clicked
    setText('New Text');
  };

  return (
    <div className="trade-card text-center justify-content-center" onClick={handleClick}>
      <h4>{text}</h4>
      {/* Rest of the card content */}
    </div>
  );
};

export default Card;
